import React from "react";
import { Modal, Box, Typography, IconButton } from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import SyntaxHighlighter from "react-syntax-highlighter";
import { docco } from "react-syntax-highlighter/dist/esm/styles/hljs";

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '90vw', // Relative width
  maxWidth: '800px', // Maximum width
  height: '90vh', // Relative height
  maxHeight: '600px', // Maximum height
  bgcolor: '#1e1e1e', // Dark background color
  color: '#fff', // White text color
  border: '1px solid #333', // Border to match dark theme
  boxShadow: 24,
  p: 4,
  borderRadius: '20px', // Rounded corners
  fontFamily: "Fira Code, monospace", // Font family
  overflow: 'auto' // Scroll content if overflow
};

function CustomModal({ explanation, coding, open, handleClose }) {


  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={style}>
        <div className="flex justify-between items-center">
          <Typography id="modal-modal-title" variant="h6" component="h2">
            {explanation}
          </Typography>
          <IconButton onClick={handleClose} sx={{color : "#fff"}}>
            <CloseIcon />
          </IconButton>
        </div>
        <Typography id="modal-modal-description" sx={{ mt: 2 }}>
          <SyntaxHighlighter language="javascript" style={docco}>
            {coding}
          </SyntaxHighlighter>
        </Typography>
      </Box>
    </Modal>
  );
}

export default CustomModal;
