import React, { useEffect, useState } from "react";
import Card from "./Card";

function Introduction() {
  const [message, setMessage] = useState({ no: "", description: "" });

  useEffect(() => {
    fetch("/api") // Ensure this matches your server endpoint
      .then((response) => response.json())
      .then((data) =>
        setMessage({ no: data.no, description: data.description })
      )
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <>

      <div className="container mx-auto flex flex-row justify-center items-center space-x-4">
        {/* For Easy Problem */}
        <div>
          <Card number={message.no} description={message.description} />
        </div>

        {/* For Medium Problem */}
        <div>
          <Card number="0" description="Longest String" />
        </div>

        {/* For Hard Problem */}
        <div>
          <Card number="0" description="Longest String" />
        </div>
      </div>
    </>
  );
}

export default Introduction;
