import React, { useEffect, useState } from "react";
import Card from "./Card";
import EasyCard from "./EasyCards";
import MediumCard from "./MediumCard";

function Introduction() {
  const [message, setMessage] = useState({ no: "", description: "" });

  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then((data) =>
        setMessage({ no: data.no, description: data.description })
      )
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const [problems, setProblems] = useState({ easy: "", medium: "", hard: "" });

  useEffect(() => {
    fetch("/get-question")
      .then((response) => response.json)
      .then((data) =>
        setProblems({ easy: data.easy, medium: data.medium, hard: data.hard }).catch((error) => console.error())
      );
  });

  return (
    <>
      <div className="container mx-auto flex flex-row justify-center space-x-4">
        {/* For Easy Problem */}
        <EasyCard />
        {/* <div>
          <Card number={message.no} description={message.description} />
        </div> */}

        {/* For Medium Problem */}
        <MediumCard />

        {/* For Hard Problem */}
        <div>
          <Card number="0" description="Longest String" />
        </div>
      </div>
    </>
  );
}

export default Introduction;
